import React, { Component } from 'react';
import axios from 'axios';

export default class Todo extends Component {
    constructor(){
        super();

        this.state={
            listofTodo: [],
            listofComments: []
        };
    }
    componentDidMount(){
        //make an API call and get the data

        axios
          .get(`https://jsonplaceholder.typicode.com/todos`)
          .then(resp => {
            debugger;
            this.setState({
                listofTodo: resp.data
            });
          })
          .catch(err => {
          });

                //   axios
                //     .get(`https://jsonplaceholder.typicode.com/todos`)
                //     .then((resp) => {
                          this.setState({
                            
                          })
                //     })
                //     .catch((err) => {
                //     });

                fetch(`https://localhost:6700/comments`, {
                  method: "GET",
                })
                  .then((resp) => resp.json())
                  .then((resp) => {
                          this.setState({
                            listofTodo: resp.data
                          });
                  })
                  .Catch((err) => {});
             }
  render() {
        const { listofTodo,listofComments } = this.state;
    return (
      <center>
        {
              listofTodo.map((item, index)=> {
                  return(
                    <ul>
                        <li>
                        { item.title }
                    </li>
                    </ul>
                  )
              })
        }
        <br/>
        <div>########## Comments ##########</div>
        {
            listofComments.map((item,index) =>{
                return(
                    <ul>
                        <li>{item.name}</li>
                    </ul>
                )   
            })
        }
      </center>
    )
  }
}
