import React, { Component } from 'react';
import axios from 'axios';

export default class Todo extends Component {
    constructor(){
        super();

        this.state={
            listofTodo: [],
            // listofComments: []
        };
    }
    componentDidMount(){
        //make an API call and get the data

        axios
          .get(`https://jsonplaceholder.typicode.com/todos`)
          .then(res => {
            debugger;
            this.setState({
                listofTodo: res
            });
          })
          .catch(err => {
            debugger;
          });
    }
  render() {
        const { listofTodo } = this.state;
    return (
      <div>
        {
              listofTodo.map((item, index)=> {
                  return(
                    <div></div>
                  )
                  
              })
        }
      </div>
    )
  }
}
