import {put,takeLatest,all} from '@redux-saga/core/effects';

function* actionWatch() {
    yield takeLatest('GET_TODOS',fetchTodos)
}

function* fetchTodos(){
  // make API call which is the async operation
  const url = 'https://jsonplaceholder.typicode.com/todos';
  const data = yield fetch(url).then(response => response.json()); //.catch(err => console.log(err));

  //update the store,put
  yield put({ type:'TODOS_RECEIVED', json: data});
}



export default function* rootSaga() { // generator function ===>yield
    yield all([
    actionWatch()
    ])
}
